import styled from 'styled-components';

export const errorStyle = styled.div `
    background: FF0000;
    border: 1px solid #f9314b;
    border-radius: 30px;
    box-sizing: border-box;
    color: #f9314b;
    font-size: 16px;
    font-weight: 600;
    height: 45px;
    outline: none;
    padding: 10px 20px;
    width: 100%;
`;

export const InputContainer = styled.div `
    text-align: center;
    font-family: 'Quicksand'
`;

export const regularStyle = styled.div `
    background: #e6d6f1;
    border: 1px solid #75468a;
    border-radius: 30px;
    box-sizing: border-box;
    color: #75468a;
    font-size: 16px;
    font-weight: 600;
    height: 45px;
    padding: 10px 20px;
    width: 100%;
`;
